parameters.functionCode:
// Validate incoming quote request data

const d = $json;

// Basic structure validation
if (!d.customer) {
  throw new Error("Missing 'customer' object in request");
}
if (!d.project) {
  throw new Error("Missing 'project' object in request");
}

// Required customer fields
const requiredCustomer = ["name", "email", "postcode"];
for (const field of requiredCustomer) {
  if (!d.customer[field] || d.customer[field].trim() === "") {
    throw new Error(`Missing required field: customer.${field}`);
  }
}

// Product validation
if (!Array.isArray(d.project.products) || d.project.products.length === 0) {
  throw new Error("Missing or invalid project.products array");
}

// Optional but recommended fields
if (!d.project.area) {
  d.project.area = "Not specified";
}
if (!d.project.budget) {
  d.project.budget = "Not specified";
}

// Normalize and clean data
d.customer.name = d.customer.name.trim();
d.customer.email = d.customer.email.toLowerCase();
d.customer.postcode = d.customer.postcode.toUpperCase().replace(/\s+/g, '');

// âœ… Pass cleaned data forward
return [{ json: d }]

