const input = $json;
const priceTable = { excavation:60, subbase:25, porcelain:130, decking:120, turf:85, planters:300, firepit:1000, lighting:200 };
let breakdown = [];
if (input.project.products.includes('patio')) breakdown.push({feature:'Porcelain Paving', qty:30, unit:'mÂ²', rate:priceTable.porcelain, subtotal:30*priceTable.porcelain});
if (input.project.products.includes('decking')) breakdown.push({feature:'Composite Decking', qty:20, unit:'mÂ²', rate:priceTable.decking, subtotal:20*priceTable.decking});
let subtotal = breakdown.reduce((a,b)=>a+b.subtotal,0);
let contingency=subtotal*0.10; let vat=(subtotal+contingency)*0.20; let total=subtotal+contingency+vat;
return [{json:{...input,breakdown,subtotal,contingency,vat,total}}];
